export const historyOptions = {
    lineHeightAnnotation: {
        alwaysStat: true,
        hover: false,
        lineWeight: 1.5,
    },

    animation: {
            duration: 2000
    },
    maintainAspectRatio: false,
    responsive: true,
    scales: {
        xAxes: [
            {
                type: 'time',
                distribution: 'linear',
            },
        ],
    },
};
