import axios from "axios";
import {API_URL, JPA_API_URL} from "../Constants";


class portfolioDataService{

    retrievePing(name){
        return axios.get(`${JPA_API_URL}/users/${name}/ping`);
    }

    retrieveAsset(data){
        return axios.get(`http://localhost:3000/users/${data}`);
    }

    retrieveAllPortfolio(name){
        return axios.get(`${JPA_API_URL}/users/${name}/userportfolio`);
    }

    createPortfolio(name, portfolio){
        return axios.post(`${JPA_API_URL}/users/${name}/userportfolio/`, portfolio);
    }

    // createTestPortfolio(name, testPortfolio){
    //     return axios.post(`${JPA_API_URL}/users/${name}/assets/`, testPortfolio);
    // }

}

export default new portfolioDataService()
