import React, {useEffect, useState} from "react";
import axios from "axios";
import { Card, CardGroup, Form, FormControl, InputGroup, Row, Col, Button } from "react-bootstrap";
//import Coin from "../Coin";
import Coin from "./CoinPortfolio";
import { NumberBox } from 'devextreme-react/number-box';
import NavigationBar from "./NavigationBar";
import ListPortfolioComponents from "./ListPortfolio";
import PortfolioDataService from "./PortfolioDataService";
import {API_URL, JPA_API_URL} from "../Constants";

class Portfolio extends React.Component {

    constructor(props){
        console.log('constructor')
        super(props)
        this.state = {
            portfolio : [],
            message : null

        }
        this.addPortfolioClicked = this.addPortfolioClicked.bind(this)
        this.pingPortfolio = this.pingPortfolio.bind(this)
    
    }
    
    refreshUserPortfolio(){
        let username = AuthenticationService.getLoggedInUserName();
        PortfolioDataService.retrieveAllPortfolio(username)
            .then(
                response => {
                    this.setState({portfolio : response.data})
                }
            )
        console.log(this.state)
    }

    // addPortfolioClicked(){
    //     this.props.history.push(`/portfolio/-1`);
    // }

    // pingPortfolio(){
    //     //PortfolioDataService.retrievePing
    //     //this.props.history.axios.get(`${JPA_API_URL}/ping`);
    //     const article = { title: 'React POST Request Example' };
    //     axios.post('http://localhost:8080/jpa/testportfolio', article)
    //         .then(response => this.setState({ articleId: response.data.id }));
    // }


    render() {
        console.log("Render");

        return <div className="text-white"> Welcome to our portfolio application! 
        <div>Here you can create your own portfolio to manage and track.</div>
        <CardGroup>
            <Card style={{ width: '30rem' }} className="border border-dark bg-dark text-white">
            <Card.Header>
                Cryptocurrency
            </Card.Header>
            <Card.Body >
                <Form>
                {/* <div className='coin-search'>
                    <h3 className='coin-text'>Search currency for portfolio</h3>
                    <form>
                        <input type='text' placeholder='Search'
                            className='coin-input' onChange={handleChange}/>
                    </form>
                </div>
                {filteredCoins.map(coin => {
                    return (<Coin
                            key={coin.id}
                            coin={coin}
                            name={coin.name}
                            image={coin.image}
                            symbol={coin.symbol}
                            price={coin.current_price}
                        />
                    );
                })} */}
                </Form>
            </Card.Body>
            </Card>
                
            <Card style={{ width: '30rem' }} className="border border-dark bg-dark text-white">
            <Card.Header>
                Quantity
            </Card.Header>
            <Form.Group className="mb-3" controlId="formGroupCrypto">
                <Form.Label>Cryptocurrency</Form.Label>
                <Form.Control />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formGroupQuantity">
                <Form.Label>Quantity</Form.Label>

             
                <Form.Control type="quantity" placeholder="Enter Quantity" />
            </Form.Group>
                
            <Card.Text>
                    
            </Card.Text>
            <Button
                className="btn btn-success" onClick={this.pingPortfolio}>
                Add to portfolio!
            </Button>
            </Card>

            <Card style={{ width: '30rem' }} className="border border-dark bg-dark text-white">
            <Card.Header>
                Portfolio
            </Card.Header>
            <Card.Text>
                This is a longer card with supporting text below as a natural
                lead-in to additional content. This content is a little bit longer.
            </Card.Text>
            <Button
                className="btn btn-success" onClick={this.refreshUserPortfolio}>
                View Portfolio
            </Button>
            {/* retrievePing(){
                return axios.get(`${JPA_API_URL}/ping`);
            } */}
            </Card>
        </CardGroup>
        </div>     
    };
}

export default Portfolio;
