import React from 'react'
import './Coin.css'
import {Link} from "react-router-dom";

const Coin = ({coin}) => {
    return (
        <Link to={`/coins/${coin.id}`}>
        <div className='coin-container'>
            <div className='coin-row'>
                {/* <div className='coin'>
                    <img src={coin.image} alt='crypto'/>
                    <h1>{coin.name}</h1>
                    <p className='coin-symbol'>{coin.symbol}</p>
                </div> */}
                <div className='coin-data'>
                    <p className='coin-data'>${coin.price}</p>
                    <p className='coin-volume'>${coin.volume.toLocaleString()}</p>
                    {coin.priceChange < 0 ? (
                        <p className='coin-percent red'>{coin.priceChange.toFixed(2)}%</p>
                    ) : (
                        <p className='coin-percent green'>{coin.priceChange.toFixed(2)}%</p>

                    )}
                    <p className='coin-marketcap'>
                        Mkt Cap: ${coin.marketcap.toLocaleString()}
                    </p>

                </div>
            </div>
        </div>
        </Link>
    );
};

export default Coin;
