import React, {useEffect, useState} from "react";
import axios from "axios";
import { Card, Form, FormControl, InputGroup, Row, Col, Button } from "react-bootstrap";
import Coin from "../Coin";
import CoinPortfolio from "./portfolio/CoinsPortfolio";

import NavigationBar from "./NavigationBar";

function Portfolio() {

    const [search, setSearch] = useState('')
    const [coins, setCoins] = useState([]);
    
    useEffect(() => {
        axios.get("https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false")
            .then(res => {
                setCoins(res.data);
                console.log(res.data);
            }).catch(error => console.log(error));
    }, []);
    
        const handleChange = e => {
            setSearch(e.target.value)
        }
        //axios.get('http://localhost:8080/assets');
        
        const filteredCoins = coins.filter(coin => coin.name.toLowerCase().includes(search.toLowerCase()))

        return (
            <div className="text-white"> Welcome to our portfolio application! 
            <div>Here you can create your own portfolio to manage and track.</div>

            <Row>
                <p></p>
                <Col xs={5}>
                <Card style={{ width: '60rem' }} className="border border-dark bg-dark text-white">
                <Card.Header>
                    Portfolio
                </Card.Header>
                <Card.Body >
                    <Form>
                    <div className='coin-search'>
                        <h3 className='coin-text'>Search currency for portfolio</h3>
                        <form>
                            <input type='text' placeholder='Search'
                                   className='coin-input' onChange={handleChange}/>
                        </form>
                    </div>
                    {filteredCoins.map(coin => {
                        return (<Coin
                                key={coin.id}
                                coin={coin}
                                name={coin.name}
                                image={coin.image}
                                symbol={coin.symbol}
                                marketcap={coin.market_cap}
                                price={coin.current_price}
                                priceChange={coin.price_change_percentage_24h}
                                volume={coin.total_volume}
                            />
                        );
                    })}
                        

                    </Form>
                </Card.Body>
                </Card>
                </Col>
            </Row>
            </div> 
        );
    
}

export default Portfolio;
